# -*- coding: utf-8 -*-
from django.conf.urls import url
from django.utils.translation import ugettext as _

from django_authopenid import views


urlpatterns = [
    # yadis rdf
    url(r'^yadis.xrdf$', views.xrdf, name='yadis_xrdf'),
     # manage account registration
    url(r'^%s$' % _('signin/'), views.signin, name='user_signin'),
    url(r'^%s$' % _('signout/'), views.signout, name='user_signout'),
    url(r'^%s%s$' % (_('signin/'), _('complete/')),
        views.complete_signin,
        name='user_complete_signin'),
    url(r'^%s$' % _('register/'), views.register, name='user_register'),
    url(r'^%s$' % _('signup/'), views.signup, name='user_signup'),
    url(r'^%s$' % _('sendpw/'), views.sendpw, name='user_sendpw'),
    url(r'^%s%s$' % (_('password/'), _('confirm/')), views.confirmchangepw,
        name='user_confirmchangepw'),

    # manage account settings
    url(r'^$', views.account_settings, name='user_account_settings'),
    url(r'^%s$' % _('password/'), views.changepw, name='user_changepw'),
    url(r'^%s$' % _('email/'), views.changeemail, name='user_changeemail'),
    url(r'^%s$' % _('openid/'), views.changeopenid, name='user_changeopenid'),
    url(r'^%s$' % _('delete/'), views.delete, name='user_delete'),
]
